mapper.select:
SELECT PHONE, USER_NM, ACCOUNT, PASSWORD
FROM K7_USER_ACCOUNT_OMS
WHERE ACCOUNT = #{ACCOUNT} AND PASSWORD = #{PASSWORD}

SELECT RN, ACCOUNT, USER_NM, USER_LV, REG_DATE, MDF_DATE, USER_CD, PASSWORD FROM( SELECT ACCOUNT, USER_NM, USER_LV, REG_DATE, MDF_DATE, USER_CD, PASSWORD, ROWNUM RN FROM( SELECT NVL(A.ACCOUNT,' ') ACCOUNT, NVL(A.USER_NM, ' ') USER_NM, NVL(B.LEVEL_EXP , ' ') USER_LV, REG_DATE, MDF_DATE, USER_CD, NVL(PASSWORD, ' ') PASSWORD FROM K7_USER_ACCOUNT_OMS A LEFT OUTER JOIN K7_USER_AUTH_OMS B ON(B.USER_LV = A.USER_LV)
WHERE USER_NM LIKE '%'||#{userNm}||'%'
ORDER BY REG_DATE DESC ) WHERE ROWNUM <= #{cri.pageNum} * #{cri.amount} ) WHERE RN > (#{cri.pageNum}-1) * #{cri.amount}

SELECT count(*)
FROM K7_USER_ACCOUNT_OMS
WHERE ACCOUNT = #{user_id}

SELECT count(*)
FROM K7_USER_ACCOUNT_OMS
WHERE USER_NM = #{user_nm}

mapper.update:
UPDATE 
	K7_USER_ACCOUNT_OMS
SET
	AUTH_NO = #{AUTH_NO}
WHERE ACCOUNT = #{ACCOUNT} AND PASSWORD = #{PASSWORD}

mapper.select:
SELECT USER_CD, USER_NM, ACCOUNT, PASSWORD, USER_LV, CPN_CD
FROM K7_USER_ACCOUNT_OMS
WHERE ACCOUNT = #{ACCOUNT} AND PASSWORD = #{PASSWORD} OR AUTH_NO = #{AUTH_NO}
